import React, { useState, useEffect }  from 'react';

function Calendar() {
    const [monthData, setMonthData] = useState([])

    console.log(Date());
    const days = daysInMonth(1, 2023); // 31
    const daysRemander = days % 7; // 3
    const weekCount = (days - daysRemander) / 7; //4 - to make how many rows

    useEffect(() => {
        for (let count=1; count <= weekCount; count++) {
            const newWeek = {
                week: count
            }
            const newMonthData = [...monthData, newWeek];
            setMonthData(newMonthData);
            // setMonthData(newMonthData);
            console.log([...Array(weekCount)]);
        }
    }, [])

    const handleCalenderWeek = [...Array(weekCount)].map((e, i) => <WeekCard key={i}/>)

    return (
        <div className="container text-center bg-white">
            <div className="row">
                <div className="col">Sunday</div>
                <div className="col">Monday</div>
                <div className="col">Tuesday</div>
                <div className="col">Wednesday</div>
                <div className="col">Thursday</div>
                <div className="col">Friday</div>
                <div className="col">Saturday</div>
            </div>
                {handleCalenderWeek}
        </div>
    );
}

function daysInMonth(month, year) {
    return new Date(year, month, 0).getDate();
}

export function WeekCard() {
    return (
        <div className="row">
            <div className="col">1</div>
            <div className="col">2</div>
            <div className="col">3</div>
            <div className="col">4</div>
            <div className="col">5</div>
            <div className="col">6</div>
            <div className="col">7</div>
        </div>
    );
}
export function DayCard(props) {
    return (
        <div className="col">{props.count}</div>
    );
}

export default Calendar;
