import React, { useState } from 'react';
import { getAuth, onAuthStateChanged } from 'firebase/auth'

function App() {
  // Stores user log in information, friends and enables more fucntionality.
  const [user, setUser] = useState({});
  const [loggedIn, setLoggedIn] = useState(false);
  // Check if User Login
  const auth = getAuth();

  const unregisterAuthListener = onAuthStateChanged(auth, (firebaseUser) => {
    if(firebaseUser) {
        console.log("loggin in", firebaseUser.displayName);
        setUser(firebaseUser);
        setLoggedIn(true);
    } else {
        console.log("logging out");
        setUser({});
        setLoggedIn(false);
    }
  })

  return (
    <div className="App">
        <img src={"https://i.etsystatic.com/12639277/r/il/955cb3/3421466341/il_1080xN.3421466341_77w0.jpg"} alt="picture of tree"/>
        <p>just checking yo</p>
    </div>
  );
}

export default App;
