import React from 'react';
import Footer from "./Footer";
import aboutUs_image from "./img/about_us.png"
import "../index.css";
const About = () => {
  return (
    <>
      <main>
        <div className="container">
          <section className="row">
            <div className="col-xs-12 col-md-12 m-3">
              <h2>About us</h2>
            </div>
            <div className="content col-xs-5 col-md-6 m-4">
              <p>Have you ever though about manaing your sleeping time? Have you ever though about manaing your sleeping time? Have you ever though about manaing your sleeping time?</p>
            </div>
            <div className="content col-xs-5 col-md-6 m-4">
              <p>Our goal is not only to make users to manage their sleep time, but also enalbe users to manage their own diary.</p>
            </div>
            <div className="col-md-5 col-xs-12">
                <img src={aboutUs_image} alt='team image'></img>
            </div>
          </section>
        </div>
      </main>
      <Footer />
    </>
  );
};

export default About;
