import React from 'react';
import Footer from "./Footer";
import "../index.css";
const About = () => {
  return (
    <>
      <main>
        <div className="container">
          <section className="row">
            <div className="col-xs-12 col-md-12 m-3">
              <h2>About us</h2>
            </div>
            <div className="content col-xs-5 col-md-6 m-4">
              <p>Have you ever thought about selling your school stuff online? This is a place where you can communicate with other students and sell them smoothly. Also, you could ask their opinions about courses while making a transaction.</p>
            </div>
            <div className="content col-xs-5 col-md-6 m-4">
              <p>Our goal is not only to make users to manage their sleep time, but also enalbe users to manage their own diary.</p>
            </div>
            <div className="col-md-5 col-xs-12">
              <img
                className="about-us-image"
                src= "img/about_us.png"
                alt="it shows the team"
              />
            </div>
          </section>
        </div>
      </main>
      <Footer />
    </>
  );
};

export default About;
